package com.importdata.transation;import java.util.ArrayList;import com.importdata.providers.ImportData.Account;import com.importdata.providers.ImportData.EnergyCost;import com.importdata.providers.ImportData.HeatIngestion;import com.importdata.providers.ImportData.Information;import com.importdata.providers.ImportData.Ingestion;import com.importdata.providers.ImportData.UnitSettings;import com.importdata.providers.ImportData.WeightChange;import android.content.ContentProviderOperation;import android.util.Log;/*** * 所有与批处理数据插入相关部分都放到这个类下面 * * */public class DataBatchInserter {    private static String TAG = "DataBatchInserter";}class HeatIngestionInserter extends DataRowModify {    public HeatIngestionInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " HeatIngestionInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(HeatIngestion.CONTENT_URI)                .withValue(HeatIngestion.FOOD_TYPE, RowData[0])                .withValue(HeatIngestion.FOOD_NAME, RowData[1])                .withValue(HeatIngestion.NAME_EXPRESS, RowData[2])                .withValue(HeatIngestion.PRIORITY, RowData[3])                .withValue(HeatIngestion.FOOD_SIZE, RowData[4])                .withValue(HeatIngestion.CALORIE, RowData[5])                .build());    }}class EnergyCostInserter extends DataRowModify {    public EnergyCostInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " EnergyCostInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(EnergyCost.CONTENT_URI)                .withValue(EnergyCost.ACCOUNT_ID, RowData[0])                .withValue(EnergyCost.METABOLISM, RowData[1])                .withValue(EnergyCost.SPORT, RowData[2])                .withValue(EnergyCost.DIGEST, RowData[3])                .withValue(EnergyCost.TRAIN, RowData[4])                .withValue(EnergyCost.ENERGY_COST_DATE, RowData[5])                .build());    }}class IngestionInserter extends DataRowModify {    public IngestionInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " IngestionInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(Ingestion.CONTENT_URI)                .withValue(Ingestion.ACCOUNT_ID, RowData[0])                .withValue(Ingestion.BREAKFAST, RowData[1])                .withValue(Ingestion.LUNCH, RowData[2])                .withValue(Ingestion.DINNER, RowData[3])                .withValue(Ingestion.ETC, RowData[4])                .build());    }}class WeightInserter extends DataRowModify {    public WeightInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " WeightInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(WeightChange.CONTENT_URI)                .withValue(WeightChange.ACCOUNT_ID, RowData[0])                .withValue(WeightChange.WEIGHT, RowData[1])                .withValue(WeightChange.EXPECTED_WEIGHT, RowData[2])                .build());    }}class UnitSettingsInserter extends DataRowModify {    public UnitSettingsInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " UnitSettingsInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(UnitSettings.CONTENT_URI)                .withValue(UnitSettings.UNIT_TYPE, RowData[0])                .withValue(UnitSettings.UNIT_NAME, RowData[1])                .build());    }}class InformationInserter extends DataRowModify {    public InformationInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " InformationInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(Information.CONTENT_URI)                .withValue(Information.ACCOUNT_ID, RowData[0])                .withValue(Information.GENDER, RowData[1])                .withValue(Information.HEIGHT, RowData[2])                .withValue(Information.BIRTHDAY, RowData[3])                .withValue(Information.CUSTOM, RowData[4])                .build());    }}class AccountInserter extends DataRowModify {    public AccountInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " AccountInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(Account.CONTENT_URI)                .withValue(Account.ACCOUNT_NAME, RowData[0])                .withValue(Account.ACCOUNT_ID, RowData[1])                .withValue(Account.ACCOUNT_PASSWORD, RowData[2])                .build());    }}